,this.kernel.clips.tabIndex=e[t]??e.clips}try{if(void 0!==$context.query.set){let t=$context.query.set;if(""===t.trim())throw Error("cannot set empty content");this.kernel.clips.addItem(t),$intents.finish()}else if(void 0!==$context.query.get){let t=$context.query.get??0;if("number"!=typeof t)throw Error("`get` must be a number index");let e=this.kernel.clips.getByIndex(t);$intents.finish(e.text)}else $intents.finish("`get` or `set` is required")}catch(t){$intents.finish(t.message)}finally{$intents.finish()// 防止卡住
}}}t.exports={run:()=>{//AppUI.renderKeyboardUI();return
