$app.env===$env.today?r.renderTodayUI():$app.env===$env.keyboard?r.renderKeyboardUI():$app.env===$env.siri&&r.shortcuts()}}}),$app.strings={en:{ALERT_INFO:"Alert",NONE:"Nothing",DONE:"Done",CLOSE:"Close",FAILED_TO_LOAD_VIEW:"Faild to load view",VIEW_NOT_PROVIDED:"The view is not provided",UNCATEGORIZED:"Uncategorized",SHARE:"Share",CLICK_TO_OPEN_JSBOX:"Click the title to open the main app.",CLIPS:"Clips",CLIPBOARD:"Clipboard",UNIVERSAL_CLIPBOARD:"Universal Clipboard",UNIVERSAL_CLIPBOARD_TIPS:"Universal Clipboard allows you to copy something on your iPhone, and paste it on your Mac–or vice-versa–using iCloud.",CLIPS_STRUCTURE_ERROR:"Clips data structure is abnormal",CLIPBOARD_NO_CHANGE:"Clipboard no change",RECYCLE_BIN:"Recycle Bin",ADD:"Add",TAG:"Tag",ADD_TAG:"Add Tag",EDIT:"Edit",SEARCH:"Search",SEARCH_HISTORY:"Search History",SEARCH_RESULT:"Search Result",NO_SEARCH_RESULT:"No item found.",FAVORITE:"Favorite",COPY:"Copy",COPIED:"Copied",SORT:"Sort",ACTIONS:"Actions",MORE_ACTIONS:"More Actions",PREVIEW:"Preview",MAX_ITEM_LENGTH:"Line Limit",TEXT_MAX_LENGTH:"Display Character Length",AUTO_SAVE:"Auto Save",AUTO_SYNC:"Auto Sync",SYNC_NOW:"Sync Now",UNZIP_FAILED:"Unzip file failed",REBUILD:"Rebuild",REBUILD_DATABASE:"Rebuild Database",REBUILD_DATABASE_ALERT:"Rebuilding the database will lose the order information, do you want to confirm the rebuild?",DELETE_ALL_DATA:"Delete All Data",DELETE_ALL_DATA_ALERT:"Are you sure you want to delete all your data?",DELETE_DATA:"Delete Data",DELETE_TABLE:"Delete all data from `${table}`?",SELECT_ALL:"Select All",DESELECT_ALL:"Deselect All",EDITOR:"Editor",CREATE_NEW:"Create New",CREATE_NEW_ACTION:"New Action",CREATE_NEW_TYPE:"New Category",TYPE_ALREADY_EXISTS:"This category already exists",EDIT_DETAILS:"Edit Details",EDIT_SCRIPT:"Edit Script",INFORMATION:"Information",NAME:"Name",ICON:"Icon",TYPE:"Category",DESCRIPTION:"Description",CODE:"Code",TEXT_INSETS:"Text bottom margin",SHOW_LINE_NUMBER:"Show line number",LIGHT_MODE_THEME:"Li